import React, { useEffect, useState } from 'react';
import '../GameContentModal/GameContent.css'; // Đảm bảo import CSS đúng tên
import coin from '../../assets/img/play-page/coin.png';
import { useSelector, useDispatch } from 'react-redux';
import { fetchData } from '../../utils/api'; // Import fetchData từ utils/api
import { processData } from '../../utils/dataUtils'; // Import processData từ utils/dataUtils

const GameContentModal = () => {
    const dispatch = useDispatch();
    const showModal = useSelector((state) => state.modal.showModal);
    const [products, setProducts] = useState([]); // State để lưu danh sách sản phẩm

    useEffect(() => {
        if (showModal) {
            fetchData('src/assets/data.json') // Gọi fetchData để tải dữ liệu
                .then((data) => {
                    processData(data, dispatch); // Xử lý dữ liệu và dispatch action
                    setProducts(data.products); // Lưu danh sách sản phẩm vào state
                })
                .catch((error) => {
                    console.error('Error fetching data:', error);
                });
        }
    }, [showModal, dispatch]);

    if (!showModal) return null;

    return (
        <div className="game-content-modal" display={{block}}>
            <div className="product-card-list">
                {/* Hiển thị danh sách sản phẩm */}
                {products.map((product, index) => (
                    <div key={index} className="product-card">
                        <img src={product.url} alt={product.title} className="product-card-img" />
                        <div className="number-correct">
                            <span>x</span>
                            <span>0</span>
                        </div>
                        <span className="product-card-name">{product.title}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default GameContentModal;
