import React, { useEffect } from 'react';
import './GameContent.css'; // Đảm bảo import CSS đúng tên
import coin from '../../assets/img/play-page/coin.png';
import { fetchData } from '../../utils/api';
import { processData } from '../../utils/dataUtils';
import { useSelector, useDispatch } from 'react-redux';

const GameContent = () => {
    const dispatch = useDispatch();
    const showGameContent = useSelector((state) => state.game.showGameContent);

    useEffect(() => {
        if (showGameContent) {
            fetchData('src/assets/data.json')
                .then((data) => {
                    processData(data, dispatch);
                    // Trigger event 'dataLoaded' with processed data
                    $(document).trigger('dataLoaded', [data]);
                })
                .catch((error) => {
                    console.error('Error fetching data:', error);
                });
        }
    }, [showGameContent, dispatch]);

    // Listen for 'dataLoaded' event and render product cards
    useEffect(() => {
        const onDataLoaded = (event, data) => {
            // Duyệt qua danh sách sản phẩm trong data
            data.forEach(product => {
                // Tạo JSX cho mỗi sản phẩm
                const productCard = (
                    <div key={product.id} className="col-xl-3 col-lg-3 col-md-6 col-sm-6 col-xs-6 product-list-content">
                        <div className="product-card">
                            <img src={product.url} alt={product.title} className="product-card-img" />
                            <div className="number-correct">
                                <span>x</span>
                                <span>0</span>
                            </div>
                            <span className="product-card-name">{product.title}</span>
                        </div>
                    </div>
                );

                // Thêm sản phẩm vào danh sách sản phẩm trong HTML
                document.querySelector('.product-card-list').appendChild(productCard);
            });
        };

        // Đăng ký sự kiện 'dataLoaded' và thực hiện onDataLoaded khi có sự kiện này xảy ra
        $(document).on('dataLoaded', onDataLoaded);

        // Xóa bỏ sự kiện khi component bị unmount để tránh memory leak
        return () => {
            $(document).off('dataLoaded', onDataLoaded);
        };
    }, []); // Chỉ cần chạy một lần khi component được render

    if (!showGameContent) return null;

    return (
        <div className="game-content">
            <div className="play-top-site row">
                <div className="play-top-site-left col-xl-6 col-lg-6 col-sm-6">
                    <div className="timer-site">
                        <div className="timer-range"></div>
                        <div className="level-site">
                            Level: <span id="level">1</span>
                        </div>
                    </div>
                </div>
                <div className="play-top-site-right col-xl-6 col-lg-6">
                    <div className="score-site">
                        <img className="crown-img" src={coin} alt="coin" />
                        <div className="current-score">0</div>
                    </div>
                </div>
            </div>

            <div className="row product-card-list"></div>
            <div className="row character-card-list"></div>
        </div>
    );
}

export default GameContent;
