import './styles/App.css';

import Header from './components/Header/Header';
import Main from './components/Main/Main';
import ErroModal from './components/ErrorModal/ErrorModal';

import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  updateProducts,
  updateCharacters,
  updateProductNames,
  updateLevels,
  updateTimeStepPercentage,
  resetGame,
  incrementScore,
  incrementLevel,
} from '../src/store/gameSlice'; // Import các action creator từ file './store'
import { 
  selectCountdownTime, 
  selectShowCountdownModal, 
  selectIntervalId 
} from '../src/store/';
import { fetchData } from '../src/utils/api'; // Hàm tải dữ liệu
import { processData } from '../src/utils/dataUtils';

const Game = () => {
  const dispatch = useDispatch();
  const listProducts = useSelector(selectListProducts);
  const listCharacters = useSelector(selectListCharacters);

  useEffect(() => {
    // Fetch data and dispatch actions to update state
    fetch('/path/to/data.json')
      .then(response => response.json())
      .then(data => {
        dispatch(setProducts(data.products));
        dispatch(setCharacters(data.characters));
        dispatch(setGameSound(data.gameSound));
        dispatch(setGameTime(data.gameTime));
      });

    // Hide other content except home page
    $('.main-bottom-content > *').not('.home-page').css("display", "none");
    $('.home-page h1').css("display", "flex");

    // Register event handlers
    $('.takashimaya-header-logo').on('click', () => {
      $('.main-bottom-content > *').not('.home-page').css("display", "none");
      $('.home-page').css("display", "flex");
    });

    $('.nav-link[href="#play-page"]').on('click', (e) => {
      e.preventDefault();
      $('.game-content').not('.header-site').css('pointer-events', '').addClass('active');
      $('.home-page h1').css("display", "none");
      $('.main-bottom-content > *').not('.main-bottom-content-row').css("display", "none");
      $('.main-bottom-content-row').css("display", "block");
      $('.game-content').css("display", "none");
      $('#count-down').load('./view/count-down.html', () => {
        $('#count-down').css("display", "block");
        dispatch(setShowCountdownModal(true));
      });
    });

  }, [dispatch]);
  return (
    <div className="wrapper">
      <Header />
      <Main />
      <ErroModal />
    </div>
  );
}

export default Game;
