const initialState = {
    showGameContent: false,
    products: [],
    characters: [],
    levels: [],
  };
  
  const gameReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_GAME_DATA':
        return {
          ...state,
          products: action.payload.products,
          characters: action.payload.characters,
          levels: action.payload.levels,
        };
      default:
        return state;
    }
  };
  
  export default gameReducer;
