import React, { useEffect } from 'react';
import '../ErrorModal/ErrorModal.css';
import bgError from '../../assets/img/general/bg-error.png';
import { useDispatch, useSelector } from 'react-redux';
import { hideErrorModal, setShowResultGame } from '../../store/';

const ErrorModal = () => {
  const dispatch = useDispatch();
  const showErrorModal = useSelector((state) => state.game.showErrorModal);

  useEffect(() => {
    if (showErrorModal) {
      // Hide the error modal after 3 seconds and show the result game
      setTimeout(() => {
        dispatch(hideErrorModal());
        dispatch(setShowResultGame(true));
      }, 3000);
    }
  }, [showErrorModal, dispatch]);

  if (!showErrorModal) return null;

  return (
    <div className="false-clicked-modal" style={{ display: showErrorModal ? 'flex' : 'none' }}>
      <img src={bgError} alt="false-clicked" className="false-clicked-img" />
    </div>
  );
};

export default ErrorModal;
