import React, { useEffect } from 'react';
import '../ErrorModal/ErrorModal.css'
import bgError from '../../assets/img/general/bg-error.png';
import { hideErrorModal } from './ErrorModalSlice';
import { showResultGame } from '../GameContentModal/GameContentModalSlice';
import { clearGameInterval } from '../GameContentModal/GameContentModalSlice';
import { useSelector, useDispatch } from 'react-redux';
import audioManager from '../../utils/audioManager';

const ErrorModal = () => {
    const dispatch = useDispatch();
    const showErrorModal = useSelector((state) => state.errorModal.showErrorModal);
    // const { audioBackground, audioFail } = useSelector(state => state.gameContentModal);
    const audioBackground = audioManager.setAudioBackground();
    const audioFail = audioManager.setAudioFail();

    useEffect(() => {
        if (showErrorModal) {
            // Xóa interval khi hiển thị lỗi
            audioBackground.pause();
            audioFail.play();
            dispatch(clearGameInterval());
            // Ẩn modal thông báo lỗi sau 3 giây và hiển thị kết quả game
            setTimeout(() => {
                dispatch(hideErrorModal());
                dispatch(showResultGame());
            }, 3000);

        }
    }, [showErrorModal, dispatch, audioBackground, audioFail]);

    if (!showErrorModal) return null;

    return (
        <div className="false-clicked-modal" style={{ display: showErrorModal ? 'flex' : 'none' }}>
            <img src={bgError} alt="false-clicked"
                className="false-clicked-ing" />
        </div>
    );
}

export default ErrorModal;
