import React, { useEffect, useState } from 'react';
import './GameContent.css';
import coin from '../../assets/img/play-page/coin.png';
import { useSelector, useDispatch } from 'react-redux';
import { fetchData } from '../../utils/api';
import {
  setGameData,
  showRandomNameCorrect,
  handleCorrectAnswer,
  handleIncorrectAnswer,
  resetAnswer,
  playSoundGameOver,
  showGameOver,
  incrementScore,
  updateGameLevel,
  setTimerInterval,
  decrementTimeRemainder,
  resetCountdownTime,
} from '../../store/gameSlice';

const GameContent = () => {
  const dispatch = useDispatch();

  const timeRemainder = useSelector(state => state.game.gameTime.timeRemainder);
  const timeStep = useSelector(state => state.game.gameTime.timeStep);
  const timerRangeWidth = useSelector(state => state.game.timerRangeWidth);

  const showGameContent = useSelector((state) => state.game.showGameContent);
  const listProducts = useSelector((state) => state.game.listProducts);
  const listCharacters = useSelector((state) => state.game.listCharacters);
  const nameCorrectIndex = useSelector((state) => state.game.nameCorrectIndex);
  const requiredNumberProducts = useSelector((state) => state.game.requiredNumberProducts);
  const randomNameCorrect = useSelector((state) => state.game.randomNameCorrect);

  const score = useSelector((state) => state.game.currentScore);
  const gameLevel = useSelector((state) => state.game.currentLevel);
  const gameTime = useSelector((state) => state.game.gameTime);
  const showErrorModal = useSelector((state) => state.game.showErrorModal);

  useEffect(() => {
    const fetchGameData = async () => {
      const data = await fetchData();
      dispatch(setGameData(data));
      dispatch(showRandomNameCorrect());
    };

    fetchGameData();
  }, [dispatch]);

  useEffect(() => {
    if (showGameContent) {
      const interval = setInterval(() => {
        dispatch(decrementTimeRemainder());
      }, timeStep);
      dispatch(setTimerInterval(interval));
      return () => clearInterval(interval);
    }
  }, [showGameContent, timeStep, dispatch]);

  const onProductClick = (productIndex) => {
    if (productIndex === nameCorrectIndex) {
      dispatch(handleCorrectAnswer({ productCardIndex: productIndex }));
      dispatch(incrementScore());
      dispatch(updateGameLevel());
    } else {
      dispatch(handleIncorrectAnswer());
      dispatch(showGameOver());
      dispatch(playSoundGameOver());
    }
  };

  if (!showGameContent) {
    return null;
  }

  return (
    <div className="play-content">
      <div className="wrap-level">
        <div className="level">
          <span className="score-title">Level</span>
          <span className="score-number">{gameLevel}</span>
        </div>
      </div>
      <div className="wrap-score">
        <img className="score-image" src={coin} alt="" />
        <div className="score">
          <span className="score-title">Score</span>
          <span className="score-number">{score}</span>
        </div>
      </div>
      <div className="timer-range">
        <div
          className="timer-range-color"
          style={{ width: timerRangeWidth }}
        ></div>
      </div>
      <div className="wrap-quiz">
        <div className="wrap-content-quiz">
          <div className="title">
            {`Collect ${requiredNumberProducts} ${randomNameCorrect}s`}
          </div>
          <div className="quiz-content">
            {listProducts.map((product, index) => (
              <div
                key={index}
                className={`quiz-card ${
                  product.numberCorrect >= requiredNumberProducts
                    ? 'correct'
                    : ''
                }`}
                onClick={() => onProductClick(index)}
              >
                <div className="wrap-img">
                  <img src={product.img} alt={product.title} />
                </div>
                <div className="wrap-number">
                  <span>{product.numberCorrect}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default GameContent;
