import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { playBackgroundSound, playbackRateAudioBackground } from '../../store/gameSlice'; // Đảm bảo đường dẫn đúng đến gameSlice
import Toolbar from '../Toolbar/Toolbar';
import GamecontentModal from '../GameContentModal/GameContentModal';
import ResultGameModal from '../ResultGameModal/ResultGameModal';
import CountdownModal from '../CountdownModal/CountdownModal';

function PlayPage() {
  const dispatch = useDispatch();

  useEffect(() => {
    const playSoundBackground = () => {
      dispatch(playBackgroundSound());
      document.removeEventListener('click', playSoundBackground);
    };

    document.addEventListener('click', playSoundBackground);

    return () => {
      document.removeEventListener('click', playSoundBackground);
    };
  }, [dispatch]);
    // const changePlaybackRate = (rate) => {
    //     dispatch(playbackRateAudioBackground(rate));
    // };

  return (
    <div className="main-bottom-site">
      <div className="play-page-main-bottom-site">
        <div className="container main-bottom-content-row">
          <div className="play-page" id="play-page">
            <audio className="audio-background"></audio>
            <audio className="audio-fail"></audio>

            <ResultGameModal />
            <CountdownModal />
            <GamecontentModal />
            <Toolbar />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayPage;
