import React, { useEffect } from 'react';
import '../ErrorModal/ErrorModal.css'
import bgError from '../../assets/img/general/bg-error.png';
import {playSoundGameOver, 
  showGameOver, 
  hideErrorModal, 
  showResultGame} from '../../store/gameSlice';
import {useSelector, useDispatch } from 'react-redux';

function ErrorModal() {
    const dispatch = useDispatch();
    const showErrorModal = useSelector((state) => state.game.showErrorModal);

    useEffect(() => {
      if (showErrorModal) {
          // Xóa interval khi hiển thị lỗi
          if (intervalId) {
              clearInterval(intervalId);
              dispatch(setIntervalId(null)); // Reset intervalId
          }

          // Ẩn modal thông báo lỗi sau 3 giây và hiển thị kết quả game
          setTimeout(() => {
              dispatch(hideErrorModal());
              dispatch(showResultGame());
          }, 3000);
      }
  }, [showErrorModal, intervalId, dispatch]);

  if (!showErrorModal) return null;
    return(
        <div className="false-clicked-modal" style={{ display: showErrorModal ? 'flex' : 'none' }}>
            <img src={bgError} alt="false-clicked" 
            className="false-clicked-ing"/>
        </div>
    );
}

export default ErrorModal;
