import { configureStore, createSlice } from '@reduxjs/toolkit';

const initialState = {
  // Các trạng thái khác
  numberCorrect: 0,
  nameCorrectDesc: '',
  numberCorrectDisplay: 'none',

  initTimeRemainder: 100,
  playTimes: 5,
  maxRequiredProducts: 5,
  answerDone: false,
  score: 0,

  timerInterval: null,
  timeRemainder: 100,
  timerRangeWidth: '100%',

  gameTime: {
    timerInterval: null,
    timeRemainder: 100,
    timerRangeWidth: '100%',
    timeStep: 10,
  },
  gameSound: {
    audioBackground: "/music/bg_sound.mp3",
    audioFail: "music/wrong.mp3",
    maxVolume: 1,
    audioRatio: 1,
  },
  // Tính điểm
  currentScore: 0,
  currentLevel: 1,
};

const gameSlice = createSlice({
  name: 'game',
  initialState,

  reducers: {
    // Các reducer khác

    resetAnswer: (state) => {
      state.numberCorrect = 0;
      state.nameCorrectDesc = '';
      state.numberCorrectDisplay = 'none';
      clearInterval(state.gameTime.timerInterval);
      state.gameTime.timeRemainder = state.initTimeRemainder;
      state.timerRangeWidth = state.gameTime.timeRemainder + "%";
    },
    setTimerInterval(state, action) {
      state.gameTime.timerInterval = action.payload;
    },
  },
});

export const {
  // Các action khác
  resetAnswer,
  setTimerInterval,
} = gameSlice.actions;

export const store = configureStore({
  reducer: {
    game: gameSlice.reducer,
  },
});
