import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
    setCountdownTime,
    toggleCountdownModal,
    setIntervalId,
    toggleGameContent,
} from '../../store/gameSlice';
import '../CountdownModal/CountdownModal.css'

const CountdownModal = () => {
    const dispatch = useDispatch();
    const countdownTime = useSelector((state) => state.game.countdownTime);
    const showCountdownModal = useSelector((state) => state.game.showCountdownModal);
    const intervalId = useSelector((state) => state.game.intervalId);

    const [showHomePageH1, setShowHomePageH1] = useState(true);
    const [showMainBottomContentRow, setShowMainBottomContentRow] = useState(false);
    const [showGameContent, setShowGameContent] = useState(true);

    useEffect(() => {
        // Kiểm tra nếu modal đếm ngược thời gian cần được hiển thị

        if (showCountdownModal) {
            const initCountDownTime = 3;
            dispatch(setCountdownTime(initCountDownTime));

            const id = setInterval(() => {
                dispatch((dispatch, getState) => {
                    // Lấy giá trị thời gian đếm ngược từ state
                    const { game } = getState();
                    const newTime = game.countdownTime - 1;

                    // Kiểm tra nếu thời gian đếm ngược đã về 0
                    if (newTime <= 0) {
                        clearInterval(game.intervalId);
                        dispatch(setIntervalId(null));
                        // Tắt modal đếm ngược
                        dispatch(toggleCountdownModal(false));
                        // Hiển thị nội dung trò chơi
                        dispatch(toggleGameContent(true));
                    } else {
                        dispatch(setCountdownTime(newTime));
                    }
                });
            }, 1000);
            // Lưu id của interval vào state
            dispatch(setIntervalId(id));
        }
        return () => {
            if (intervalId) {
                clearInterval(intervalId);
                dispatch(setIntervalId(null));
            }
        };
    }, [showCountdownModal, dispatch]);

    // Nếu modal đếm ngược không cần được hiển thị, không render component
    if (!showCountdownModal) return null;
    return (
        <div className="modal-count-down">
            <span>Count Down Game</span>
            <h1 className="count-down-text">{countdownTime}</h1>
        </div>

    );
}
export default CountdownModal;
