import './styles/App.css';

import Header from './components/Header/Header';
import Main from './components/Main/Main';
import ErroModal from './components/ErrorModal/ErrorModal';

import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  updateProducts,
  updateCharacters,
  updateProductNames,
  updateLevels,
  updateTimeStepPercentage,
  resetGame,
  incrementScore,
  incrementLevel,
} from '../src/store/gameSlice'; // Import các action creator từ file './store'
import { fetchData } from '../src/utils/api'; // Hàm tải dữ liệu
import { processData } from '../src/utils/dataUtils';

const Game = () => {
  const dispatch = useDispatch();
  const { 
    listProducts, 
    listCharacters, 
    listProductNames, 
    levels, 
    currentScore, 
    currentLevel 
  } = useSelector((state) => state.game);

    useEffect(() => {
        fetchData('src/assets/data.json')
            .then((data) => {
                processData(data, dispatch);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, [dispatch]);


  // Các hàm xử lý game
  const handleCorrectAnswer = (productTitle) => {
    // Xử lý khi người chơi chọn đúng
    dispatch(incrementScore());
    if (currentScore % 3 === 0 && currentScore > 0) {
      dispatch(incrementLevel());
      // Cập nhật timeStepDenominator từ levels
      const levelData = levels.find((level) => level.level === currentLevel);
      if (levelData) {
        // Cập nhật timeStepDenominator và timeStep
        dispatch(updateTimeStepPercentage(levelData.timeStepPercentage));
      }
    }
  };

  const handleIncorrectAnswer = () => {
    // Xử lý khi người chơi chọn sai
    console.log('You choosed incorrect!');
    // Dừng âm thanh nền
    // dispatch(pauseBackgroundAudio());
    // Hiển thị màn hình kết thúc game
    // dispatch(showGameOver());
    // Phát âm thanh khi game over
    // dispatch(playGameOverSound());
  };

  // JSX của Game Component
  return (
    <div className="wrapper">
      <Header />
      <Main />
      <ErroModal />
    </div>
  );
}

export default Game;
