import React, { useEffect, useState } from 'react';
import './GameContent.css';
import coin from '../../assets/img/play-page/coin.png';
import { useSelector, useDispatch } from 'react-redux';
import { fetchData } from '../../utils/api';
import {
  setGameData,
  showRandomNameCorrect,
  handleCorrectAnswer,
  handleIncorrectAnswer,
  resetAnswer,
  playSoundGameOver,
  showGameOver,
  incrementScore,
  updateGameLevel,
  setTimerInterval,
  decrementTimeRemainder,
  resetCountdownTime,
  setCountdownTime,
  updateTimeRemainder,
} from '../../store/gameSlice';

const GameContent = () => {
  const dispatch = useDispatch();

  const timeRemainder = useSelector(state => state.game.gameTime.timeRemainder);
  const timeStep = useSelector(state => state.game.gameTime.timeStep);
  const timerRangeWidth = useSelector(state => state.game.timerRangeWidth);

  const { numberCorrect, nameCorrectDesc, numberCorrectDisplay, timerInterval } = useSelector((state) => state.game);

  const showGameContent = useSelector((state) => state.game.showGameContent);
  const listProducts = useSelector((state) => state.game.listProducts);
  const listCharacters = useSelector((state) => state.game.listCharacters);
  const nameCorrectIndex = useSelector((state) => state.game.nameCorrectIndex);
  const requiredNumberProducts = useSelector((state) => state.game.requiredNumberProducts);
  const randomNameCorrect = useSelector((state) => state.game.randomNameCorrect);

  const score = useSelector((state) => state.game.currentScore);
  const gameLevel = useSelector((state) => state.game.currentLevel);
  const gameTime = useSelector((state) => state.game.gameTime);
  const [characters, setCharacters] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    if (showGameContent) {
      fetchData(process.env.PUBLIC_URL + '/data.json')
        .then((data) => {
          const processedData = {
            products: data.products.map(product => ({
              ...product,
              url: data.productsAssetsPath + product.url,
            })),
            characters: data.characters.map(character => ({
              ...character,
              url: data.charactersAssetsPath + character.url,
              activeUrl: data.charactersAssetsPath + character.activeUrl,
            })),
            levels: data.levels || [],
          };
          dispatch(setGameData(processedData));
          setCharacters(processedData.characters);
          setProducts(processedData.products);
          dispatch(showRandomNameCorrect());
          
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
        });
    }
  }, [showGameContent, dispatch]);

  useEffect(() => {
    if (showGameContent) {
      dispatch(showRandomNameCorrect());
    }
  }, [showGameContent, dispatch]);

  useEffect(() => {
    const getAnswer = () => {
      document.querySelectorAll('.product-card').forEach(card => {
        card.addEventListener('click', (event) => {
          const productCard = event.currentTarget;
          const requireProduct = document.querySelector('.name-correct-desc').innerText.toLowerCase();
          const productTitle = productCard.querySelector('.product-card-name').innerText.toLowerCase();
         
          if (productTitle === requireProduct) {
            handleCorrectAnswer(productCard);

          } else {
            handleIncorrectAnswer();
          }
        });
      });
    };

    if (products.length > 0) {
      getAnswer();
    }
  }, [products]);

  const handleCorrectAnswer = (productCard) => {
    // Lấy phần tử con cuối cùng của ".number-correct span"
    const numberCorrectElement = productCard.querySelector('.number-correct span:last-child');
    // Tăng số lượng câu trả lời đúng lên 1
    const newValue = Number(numberCorrectElement.innerText) + 1;
    numberCorrectElement.innerText = newValue;
    // Hiển thị phần tử ".number-correct"
    productCard.querySelector('.number-correct').style.display = "flex";
    if (newValue > requiredNumberProducts) {
      // Người dùng đã chọn nhiều sản phẩm hơn yêu cầu
      console.log('Bạn đã chọn quá số lượng!');
      dispatch(showGameOver());
    } else if (newValue === requiredNumberProducts) {
      // Người dùng đã chọn đúng số lượng sản phẩm yêu cầu
      console.log('Bạn đã chọn đúng');
      setTimeout(() => {
        // Đặt lại câu trả lời
        dispatch(resetAnswer());
        // Hiển thị tên-đúng ngẫu nhiên mới
        dispatch(showRandomNameCorrect());
        // Cập nhật cấp độ game
        dispatch(updateGameLevel());
        dispatch(incrementScore());
        dispatch(updateGameLevel());
      }, 300);
    }
  };

  const handleIncorrectAnswer = () => {
    console.log('You choosed incorrect!');
    // if (audioBackground) {
    //   audioBackground.pause();
    // }
    dispatch(playSoundGameOver());
    // dispatch(toggleCountdownModal());getAnswer
  };

  useEffect(() => {
    dispatch(resetCountdownTime());
    const timerInterval = setInterval(() => {
      // // dispatch(decrementTimeRemainder());
    }, 1000);

    dispatch(setTimerInterval(timerInterval));

    return () => {
      clearInterval(timerInterval);
      dispatch(setTimerInterval(null));
    };
  }, [dispatch, timeStep]);

  useEffect(() => {
    if (timeRemainder <= 0) {
      dispatch(showGameOver());
      dispatch(playSoundGameOver());
    }
  }, [timeRemainder, dispatch]);

  // useEffect(() => {
  //   // Example to start the timer
  //   const interval = setInterval(() => {
  //     dispatch(updateTimeRemainder(timeRemainder - 1));
  //   }, 1000);
  //   dispatch(setTimerInterval(interval));

  //   return () => clearInterval(interval); // Clean up interval on component unmount
  // }, [dispatch, timeRemainder]);

  const handleResetAnswer = () => {
    dispatch(resetAnswer());
  };

  if (!showGameContent) return null;

  return (
    <div className="game-content" style={{ display: showGameContent ? 'flex' : 'none' }}>
      <div className="play-top-site row">
        <div className="play-top-site-left col-xl-6 col-lg-6 col-sm-6">
          <div className="timer-site">
            <div className="timer-range" style={{ width: timerRangeWidth }}></div>
            <div className="level-site">
              Level: <span id="level">{gameLevel}</span>
            </div>
          </div>
        </div>
        <div className="play-top-site-right col-xl-6 col-lg-6">
          <div className="score-site">
            <img className="crown-img" src={coin} alt="coin" />
            <div className="current-score">{score}</div>
          </div>
        </div>
      </div>

      <div className="row product-card-list" style={{ marginLeft: '0', marginRight: '0' }}>
        {products.map((product, index) => (
          <div key={index} className="col-xl-3 col-lg-3 col-md-6 col-sm-6 col-xs-6 product-list-content">
            <div className="product-card">
              <img src={`${process.env.PUBLIC_URL}${product.url}`} alt={product.title} className="product-card-img" />
              <div className="number-correct" style={{ display: numberCorrectDisplay }}>
                <span>x</span>
                <span>0</span>
              </div>
              <span className="product-card-name">{product.title}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="row character-card-list" style={{ marginLeft: '0', marginRight: '0' }}>
        {characters.map((character, index) => (
          <div key={index} className="col-xl-3 col-lg-3 col-md-4 col-sm-6 col-xs-6 character-list-content">
            <img
              src={character.url}
              alt={`character-${index + 1}-card`}
              className={`character-card character-${index + 1}-card`}
              style={{ display: index === nameCorrectIndex ? 'none' : 'block' }}
            />
            <img
              src={character.activeUrl}
              alt={`character-${index + 1}-card-active`}
              className={`character-card character-${index + 1}-card-active`}
              style={{ display: index === nameCorrectIndex ? 'block' : 'none' }}
            />
            <div className="name-correct" style={{ display: index === nameCorrectIndex ? 'block' : 'none' }}>
              <span className="name-correct-number" style={{ marginRight: '5px' }}>{requiredNumberProducts}</span>
              <span className="name-correct-desc">{randomNameCorrect}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GameContent;
