import { createSlice } from '@reduxjs/toolkit';

const errorModalInitialState = {
    audioBackgroundPaused: false,
    showErrorModal: false,
    intervalId: null,
    timerInterval: null,
    audioBackground: new Audio("/music/bg_sound.mp3"),
    audioFail: new Audio("/music/wrong.mp3"),
};

const errorModalSlice = createSlice({
    name: 'errorModal',
    initialState: errorModalInitialState,
    reducers: {
       
        hideErrorModal(state) {
            state.showErrorModal = false;
        },
    },
});

export const { showGameOver, hideErrorModal, clearGameInterval } = errorModalSlice.actions;

export default errorModalSlice.reducer;
