import React, { useEffect } from 'react';
import '../ErrorModal/ErrorModal.css'
import bgError from '../../assets/img/general/bg-error.png';
import {playSoundGameOver, showGameOver, hideErrorModal, showResultGame} from '../../store/gameSlice';
import {useSelector, useDispatch } from 'react-redux';

function ErrorModal() {
    const dispatch = useDispatch();
    const showErrorModal = useSelector((state) => state.game.showErrorModal);

    useEffect(() => {
        if (showGameOver) {
            dispatch(playSoundGameOver());

            setTimeout(() => {
                dispatch(hideErrorModal());
                dispatch(showResultGame());
            }, 3000);
        }
    }, [showGameOver, dispatch]);

    if (!showGameOver) return null;
    return(
        <div className="false-clicked-modal" style={{ display: showGameOver ? 'flex' : 'none' }}>
            <img src={bgError} alt="false-clicked" 
            className="false-clicked-ing"/>
        </div>
    );
}

export default ErrorModal;
