import React, { useEffect } from 'react';
import bgStart from '../../assets/img/general/bg-start.png';
import '../ResultGameModal/ResultGameModal.css';
import { useSelector, useDispatch } from 'react-redux';
import { hideResultModal } from './ResultGameModalSlice';

const ResultGameModal = () => {
    const dispatch = useDispatch();

    const showResultGameModal = useSelector(state => state.resultGameModal.showResultGameModal);
    const finalScore = useSelector(state => state.gameContentModal.finalScore);

     // Result Game
     useEffect(() => {
      const modalElement = document.querySelector('.modal-result-game');
      const finalScoreElement = document.querySelector('.final-score-text');

      if (showResultGameModal) {
          modalElement.style.display = 'flex';
          finalScoreElement.innerText = finalScore;

          const handleRestartGame = () => {
              dispatch(hideResultModal());
          };
          const handleCloseModal = () => {
              dispatch(hideResultModal());
          };
          document.querySelector('.restart-game-text').addEventListener('click', handleRestartGame);
          document.querySelector('.result-game-button-close').addEventListener('click', handleCloseModal);
          return () => {
              document.querySelector('.restart-game-text').removeEventListener('click', handleRestartGame);
              document.querySelector('.result-game-button-close').removeEventListener('click', handleCloseModal);
          };
      }
  }, [showResultGameModal, finalScore, dispatch]);

  
    return (
        <div className="modal-result-game">
            <div className="result-game-wrapper">
                <div className="result-game-header">
                    <img src={bgStart} alt="Game result" />
                    <button className="result-game-button-close"></button>
                    <h2 id="your-own-score">Your Own Score</h2>
                </div>
                <h1 className="final-score-text">0</h1>
                <div className="choices-group">
                    <button className="primary-button cancel-game-text">Cancel game</button>
                    <button className="primary-button restart-game-text">Restart game</button>
                </div>
                <div className="register-toggle-label">
                    <p>Register</p>
                </div>
            </div>
        </div>
    );
}
export default ResultGameModal;
