import React from 'react';

import GamecontentModal from '../GameContentModal/GameContentModal';
import ResultGameModal from '../ResultGameModal/ResultGameModal';
import CountdownModal from '../CountdownModal/CountdownModal';
import bgPageContent from '../../assets/img/general/bg-page-content.png';

function PlayPage() {
  useEffect(() => {
    // Tạo một thẻ img ảo để kiểm tra khi ảnh được tải
    const img = new Image();
    img.src = bgPageContent; // Sử dụng biến ảnh đã nhập
    
    // Đăng ký sự kiện load
    img.onload = () => {
      console.log("The background image has been loaded:", img.src);
    };

    // Đăng ký sự kiện error
    img.onerror = () => {
      console.error("Error loading the background image:", img.src);
    };
  }, []);
  return (
    <div className="main-bottom-site">
      <div className="play-page-main-bottom-site">
        <div className="container main-bottom-content-row">
        <div className="play-page-content" id="play-page" style={{
                        height: '100%',
                        minHeight: '90vh',
                        overflow: 'hidden',
                        backgroundRepeat: 'no-repeat',
                        backgroundSize: 'cover',
                        backgroundImage: 'url("/public/images/general/bg-page-content.png")',
                        borderRadius: '20px'
                    }}>
            <audio className="audio-background"></audio>
            <audio className="audio-fail"></audio>
            <ResultGameModal />
            <CountdownModal />
            <GamecontentModal />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayPage;
