import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { playBackgroundSound, setAudioBackground } from '../../gameSlice';
import Toolbar from '../Toolbar/Toolbar';
import GamecontentModal from '../GameContentModal/GameContentModal';
import ResultGameModal from '../ResultGameModal/ResultGameModal';
import CountdownModal from '../CountdownModal/CountdownModal';

const PlayPage = () => {
    const dispatch = useDispatch();
    const audioBackground = useSelector(state => state.game.audioBackground);

    useEffect(() => {
        const $audioBackgroundElement = document.querySelector('.audio-background');

        if ($audioBackgroundElement) {
            dispatch(setAudioBackground($audioBackgroundElement));
            $audioBackgroundElement.addEventListener('click', () => {
                dispatch(playBackgroundSound());
            }, { once: true });
        }
    }, [dispatch]);

    // Sử dụng audioBackground để hiển thị thông tin hoặc kiểm tra trạng thái âm thanh
    if (audioBackground) {
        console.log('Current audio background:', audioBackground);
    }

    return (
        <div className="main-bottom-site">
            <div className="play-page-main-bottom-site">
                <div className="container main-bottom-content-row">
                    <div className="play-page" id="play-page">
                        <audio className="audio-background"></audio>
                        <audio className="audio-fail"></audio>

                        <ResultGameModal />
                        <CountdownModal />
                        <GamecontentModal />
                        <Toolbar />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PlayPage;
