import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  setCountdownTime,
  toggleCountdownModal,
  setIntervalId,
  toggleGameContent,
} from '../../store/gameSlice';
import '../CountdownModal/CountdownModal.css';

const CountdownModal = () => {
  const dispatch = useDispatch();
  const countdownTime = useSelector((state) => state.game.countdownTime);
  const showCountdownModal = useSelector((state) => state.game.showCountdownModal);
  const intervalId = useSelector((state) => state.game.intervalId);

  useEffect(() => {
    if (showCountdownModal) { // Nếu modal đếm ngược được hiển thị
      const initCountDownTime = 3; // Thời gian đếm ngược ban đầu là 3 giây
      dispatch(setCountdownTime(initCountDownTime)); // Cập nhật thời gian đếm ngược trong Redux store
      const id = setInterval(() => { // Tạo một interval để giảm thời gian đếm ngược mỗi giây
        dispatch((dispatch, getState) => {
          const { game } = getState(); // Lấy state hiện tại từ Redux store
          const newTime = game.countdownTime - 1; // Giảm thời gian đếm ngược đi 1 giây
          if (newTime <= 0) { // Nếu thời gian đếm ngược bằng hoặc nhỏ hơn 0
            clearInterval(game.intervalId); // Dừng interval
            dispatch(setIntervalId(null)); // Đặt intervalId về null trong Redux store
            dispatch(toggleCountdownModal(false)); // Ẩn modal đếm ngược
            dispatch(toggleGameContent(true)); // Hiển thị nội dung game
          } else {
            dispatch(setCountdownTime(newTime)); // Cập nhật thời gian đếm ngược mới trong Redux store
          }
        });
      }, 1000);

      dispatch(setIntervalId(id)); // Lưu intervalId vào Redux store để có thể dừng nó sau này
    }
    return () => {
      if (intervalId) { // Khi component bị unmount hoặc showCountdownModal thay đổi
        clearInterval(intervalId); // Dừng interval
        dispatch(setIntervalId(null)); // Đặt intervalId về null trong Redux store
      }
    };
  }, [showCountdownModal, dispatch, intervalId]); // Chỉ chạy lại useEffect khi showCountdownModal, dispatch hoặc intervalId thay đổi

  if (!showCountdownModal) return null; // Nếu modal đếm ngược không được hiển thị, không render gì cả

  return (
    <div className="modal-count-down">
      <span>Count Down Game</span>
      <h1 className="count-down-text">{countdownTime}</h1>
    </div>
  );
}

export default CountdownModal;
