import React, { useEffect, useState } from 'react';
import './GameContent.css'; // Đảm bảo import CSS đúng tên
import coin from '../../assets/img/play-page/coin.png';
import { useSelector, useDispatch } from 'react-redux';
import { fetchData } from '../../utils/api'; // Import fetchData từ utils/api
import { processData } from '../../utils/dataUtils'; // Import processData từ utils/dataUtils

const Gamecontent = () => {
    const dispatch = useDispatch();
    const showGameContent = useSelector((state) => state.game.showGameContent);
    const [characters, setCharacters] = useState([]); // State để lưu danh sách nhân vật

    useEffect(() => {
        if (showGameContent) {
            fetchData('src\assets\data.json') // Gọi fetchData để tải dữ liệu
                .then((data) => {
                    processData(data, dispatch); // Xử lý dữ liệu và dispatch action
                    setCharacters(data.characters); // Lưu danh sách nhân vật vào state
                })
                .catch((error) => {
                    console.error('Error fetching data:', error);
                });
        }
    }, [showGameContent, dispatch]);

    if (!showGameContent) return null;

    return (
        <div class="game-content" style={{ display: 'flex' }}>
            <div class="play-top-site row">
                <div class="play-top-site-left col-xl-6 col-lg-6 col-sm-6">
                    <div class="timer-site">
                        <div class="timer-range"></div>
                        <div class="level-site">
                            Level: <span id="level">1</span>
                        </div>
                    </div>
                </div>
                <div class="play-top-site-right col-xl-6 col-lg-6">
                    <div class="score-site">
                        <img class="crown-img" src={coin} alt="coin" />
                        <div class="current-score">0</div>
                    </div>
                </div>
            </div>

            <div class="row product-card-list" style={{ marginLeft: '0', marginRight: '0' }}></div>
            <div class="row character-card-list" style={{ marginLeft: '0', marginRight: '0' }}></div>
        </div>
    );
}

export default Gamecontent;
