// utils/dataUtils.js
import '../assets/data.json'; 
export const processData = (data, dispatch) => {
    const productsAssetsPath = data.productsAssetsPath;
    const charactersAssetsPath = data.charactersAssetsPath;

    // Xử lý dữ liệu sản phẩm
    if (data && data.products && Array.isArray(data.products)) {
        const listProducts = data.products.map(product => ({
            ...product,
            url: productsAssetsPath + product.url
        }));
        const listProductNames = listProducts.map(product => product.title.toLowerCase());

        // Gửi action để cập nhật sản phẩm trong Redux store
        dispatch(updateProducts(listProducts));
        dispatch(updateProductNames(listProductNames));
    } else {
        console.error('Invalid product data format');
    }

    // Xử lý dữ liệu nhân vật
    if (data && data.characters && Array.isArray(data.characters)) {
        const listCharacters = data.characters.map(character => ({
            ...character,
            url: charactersAssetsPath + character.url,
            activeUrl: charactersAssetsPath + character.activeUrl
        }));

        // Gửi action để cập nhật nhân vật trong Redux store
        dispatch(updateCharacters(listCharacters));
    } else {
        console.error('Invalid character data format');
    }

    // Xử lý dữ liệu cấp độ
    if (data && data.levels && Array.isArray(data.levels)) {
        // Gửi action để cập nhật cấp độ trong Redux store
        dispatch(updateLevels(data.levels));
    } else {
        console.error('Invalid levels data format');
    }
};
