import React, { useEffect, useState } from 'react';
import './Gamecontent.css'; // Đảm bảo import CSS đúng tên
import coin from '../../assets/img/play-page/coin.png';
import { useSelector, useDispatch } from 'react-redux';
import { fetchData } from '../../utils/api'; // Import fetchData từ utils/api
import { processData } from '../../utils/dataUtils'; // Import processData từ utils/dataUtils

const Gamecontent = () => {
    const dispatch = useDispatch();
    const showGameContent = useSelector((state) => state.game.showGameContent);
    const [characters, setCharacters] = useState([]); // State để lưu danh sách nhân vật

    useEffect(() => {
        if (showGameContent) {
            fetchData('src/assets/data.json') // Gọi fetchData để tải dữ liệu
                .then((data) => {
                    processData(data, dispatch); // Xử lý dữ liệu và dispatch action
                    setCharacters(data.characters); // Lưu danh sách nhân vật vào state
                })
                .catch((error) => {
                    console.error('Error fetching data:', error);
                });
        }
    }, [showGameContent, dispatch]);

    if (!showGameContent) return null;

    return (
        <div className="game-content" style={{ display: 'flex' }}>
            <div className="play-top-site row">
                <div className="play-top-site-left col-xl-6 col-lg-6 col-sm-6">
                    <div className="timer-site">
                        <div className="timer-range"></div>
                        <div className="level-site">
                            Level: <span id="level">1</span>
                        </div>
                    </div>
                </div>
                <div className="play-top-site-right col-xl-6 col-lg-6">
                    <div className="score-site">
                        <img className="crown-img" src={coin} alt="coin" />
                        <div className="current-score">0</div>
                    </div>
                </div>
            </div>

            <div className="row product-card-list" style={{ margin: '0' }}></div>
            
            {/* Hiển thị danh sách nhân vật */}
            <div className="row character-card-list" style={{ margin: '0' }}>
                {characters.map((character, index) => (
                    <div key={index} className="col-xl-3 col-lg-3 col-md-4 col-sm-6 col-xs-6 character-list-content">
                        <img
                            src={character.url}
                            alt={`character-${index + 1}-card`}
                            className={`character-card character-${index + 1}-card`}
                        />
                        <img
                            src={character.activeUrl}
                            alt={`character-${index + 1}-card-active`}
                            className={`character-card character-${index + 1}-card-active`}
                        />
                        <div className="name-correct">
                            <span className="name-correct-number"></span>
                            <span className="name-correct-desc"></span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Gamecontent;
