import React, { useEffect } from 'react';
import '../ErrorModal/ErrorModal.css'
import bgError from '../../assets/img/general/bg-error.png';
import {playSoundGameOver, showGameOver} from '../../store/gameSlice';
import {useSelector, useDispatch } from 'react-redux';

function ErrorModal() {
    const dispatch = useDispatch();
    const handleGameOver = () => {
        dispatch(playSoundGameOver());
      };
      const showErrorModal = useSelector((state) => state.game.showErrorModal);

  useEffect(() => {
    if (showErrorModal) {
      // Hide the error modal after 3 seconds and show the result game
      setTimeout(() => {
        dispatch(hideErrorModal());
        dispatch(setShowResultGame(true));
      }, 3000);
    }
  }, [showErrorModal, dispatch]);

  if (!showErrorModal) return null;
    return(
        <div className="false-clicked-modal">
            <img src={bgError} alt="false-clicked" 
            className="false-clicked-ing"/>
        </div>
    );
}

export default ErrorModal;
