// store/selectors.js

export const selectListProducts = (state) => state.game.listProducts;
export const selectListCharacters = (state) => state.game.listCharacters;
export const selectGameSound = (state) => state.game.gameSound;
export const selectGameTime = (state) => state.game.gameTime;
export const selectShowCountdownModal = (state) => state.game.showCountdownModal;
export const selectCountdownTime = (state) => state.game.countdownTime;
export const selectShowGameContent = (state) => state.game.showGameContent;
export const selectIntervalId = (state) => state.game.intervalId;
