import React, { useEffect } from 'react';
import '../ErrorModal/Index.css'
import bgError from '../../assets/img/general/bg-error.png';
import { hideErrorModal } from './ErrorSlice';
import { showResultGame } from '../GameContentModal/GameContentSlice';
import { useSelector, useDispatch } from 'react-redux';
import { showGameOver } from '../ErrorModal/errorModalSlice';

const ErrorModal = () => {
    const dispatch = useDispatch();

     const handleGameOver = () => {
    dispatch(showGameOver({ dispatch })); // Truyền dispatch vào payload
  };

    const showErrorModal = useSelector((state) => state.errorModal.showErrorModal);

    useEffect(() => {
        if (showErrorModal) {
            handleGameOver();
            // Ẩn modal thông báo lỗi sau 3 giây và hiển thị kết quả game
            setTimeout(() => {
                dispatch(hideErrorModal());
                dispatch(showResultGame());
            }, 3000);

        }
    }, [showErrorModal, dispatch]);

    if (!showErrorModal) return null;

    return (
        <div className="false-clicked-modal" style={{ display: showErrorModal ? 'flex' : 'none' }}>
            <img src={bgError} alt="false-clicked"
                className="false-clicked-ing" />
        </div>
    );
}

export default ErrorModal;
