import store from '../store'; // Đường dẫn đến store của bạn
import { setScrollToElement } from '../store/gameSlice'; // Đường dẫn đến gameSlice của bạn
import $ from 'jquery'; // Import jQuery vào đây

const scrollToRandomElement = (randomNumber) => {
  const element = $(`.character-card-list > div:nth-child(${randomNumber})`)[0];
  if (element) {
    element.scrollIntoView({
      behavior: 'smooth',
      inline: 'center',
    });
    store.dispatch(setScrollToElement(element)); // Gọi action setScrollToElement với element là payload
  }
};

export { scrollToRandomElement };

export const fetchData = async (url) => {
  const response = await fetch(url);
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
  return response.json();
};
