import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { stopCountdownTime,playBackgroundSound, playbackRateAudioBackground } from '../../store/gameSlice'; // Đảm bảo đường dẫn đúng đến gameSlice
import Toolbar from '../Toolbar/Toolbar';
import GamecontentModal from '../GameContentModal/GameContentModal';
import ResultGameModal from '../ResultGameModal/ResultGameModal';
import CountdownModal from '../CountdownModal/CountdownModal';
import GameContentModal from '../GameContentModal/GameContentModal';

function PlayPage() {
  const showGameContent = useSelector(state => state.game.showGameContent);

  useEffect(() => {
    if (!showGameContent) {
      dispatch(stopCountdownTime());
    }
  }, [dispatch, isGameContentRunning]);

  // useEffect(() => {
