import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { 
  toggleGameContent, 
  playBackgroundSound, 
  setTimerInterval, 
  clearGameInterval 
} from '../GameContentModal/GameContentModalSlice';
import { 
  setCountdownTime, 
  toggleCountdownModal 
} from './CountdownModalSlice';
import '../CountdownModal/CountdownModal.css';

const CountdownModal = () => {
  const dispatch = useDispatch();
  const countdownTime = useSelector((state) => state.countdownModal.countdownTime);
  const showCountdownModal = useSelector((state) => state.countdownModal.showCountdownModal);
  const timerInterval = useSelector((state) => state.gameContentModal.timerInterval);

  useEffect(() => {
    if (showCountdownModal) { // Nếu modal đếm ngược được hiển thị
      const initCountDownTime = 3;
      // console.clear();
      dispatch(playBackgroundSound());
      dispatch(setCountdownTime(initCountDownTime));
      const id = setInterval(() => { // Interval giảm thời gian đếm ngược
        dispatch((dispatch, getState) => {
          const { countdownModal } = getState();
          const newTime = countdownModal.countdownTime - 1;
          if (newTime <= 0) {
            clearInterval(timerInterval);
            dispatch(setTimerInterval(null));
            dispatch(toggleCountdownModal(false));
            dispatch(toggleGameContent(true));
          } else {
            dispatch(setCountdownTime(newTime));
          }
        });
      }, 1000);

      dispatch(setTimerInterval(id));
    }
    return () => {
      if (timerInterval) {
        clearInterval(timerInterval);
        dispatch(setTimerInterval(null));
      }
    };
  }, [showCountdownModal, dispatch, timerInterval]);

  if (!showCountdownModal) return null;
  return (
    <div className="modal-count-down">
      <span>Count Down Game</span>
      <h1 className="count-down-text">{countdownTime}</h1>
    </div>
  );
};

export default CountdownModal;
