import { createSlice } from '@reduxjs/toolkit';

const finalScore  = useSelector(state => state.gameContentModal.finalScore);
const resultGameModalInitialState = {
    showResultGameModal: false,
  };
  
  const resultGameModalSlice = createSlice({
    name: 'resultGameModal',
    initialState: resultGameModalInitialState,
    reducers: {
        showResultGame(state) {
            state.showResultGameModal = true;
            state.finalScore = state.currentScore;
          },
      hideResultModal(state) {
        state.showResultGameModal = false;
      },
      
    },
  });
  

export const { showResultGame, hideResultModal } = resultGameModalSlice.actions;

export default resultGameModalSlice.reducer;
