import React, { useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toggleGameContent } from '../GameContentModal/GameContentSlice';
import {
  setCountdownTimeModal,
  toggleCountdownModal,
  setIntervalId,
} from './CountdownSlice';
import '../CountdownModal/Index.css';

const CountdownModal = () => {
  const dispatch = useDispatch();
  const countdownTime = useSelector((state) => state.countdownModal.countdownTime);
  const showCountdownModal = useSelector((state) => state.countdownModal.showCountdownModal);
  const intervalIdRef = useRef(null);

  useEffect(() => {
    if (showCountdownModal) { // Nếu modal đếm ngược được hiển thị
      const initCountDownTime = 3;
      console.clear();

      dispatch(setCountdownTimeModal(initCountDownTime));

      intervalIdRef.current = setInterval(() => { // Interval giảm thời gian đếm ngược
        dispatch((dispatch, getState) => {
          const { countdownTime } = getState().countdownModal;
          const newTime = countdownTime - 1;
          if (newTime <= 0) {
            clearInterval(intervalIdRef.current);
            dispatch(setCountdownTimeModal(0)); // Đảm bảo countdownTime là 0 khi kết thúc
            dispatch(toggleCountdownModal(false));
            dispatch(toggleGameContent(true));
          } else {
            dispatch(setCountdownTimeModal(newTime));
          }
        });
      }, 1000);

      dispatch(setIntervalId(intervalIdRef.current)); // Lưu intervalId vào Redux store
    }
    
    return () => {
      if (intervalIdRef.current) {
        clearInterval(intervalIdRef.current);
        dispatch(setIntervalId(null)); // Xóa intervalId khỏi Redux store
      }
    };
  }, [showCountdownModal, dispatch]);

  if (!showCountdownModal) return null;

  return (
    <div className="modal-count-down">
      <span>Count Down Game</span>
      <h1 className="count-down-text">{countdownTime}</h1>
    </div>
  );
};

export default CountdownModal;
