import React, { useEffect } from 'react';
import bgStart from '../../assets/img/general/bg-start.png';
import '../ResultGameModal/ResultGameModal.css';
import { useSelector, useDispatch } from 'react-redux';
import { hideResultGame } from '../GameContentModal/GameContentModalSlice';
import { toggleCountdownModal } from '../CountdownModal/CountdownModalSlice';
import { toggleGameContent, resetGame } from '../GameContentModal/GameContentModalSlice';

const ResultGameModal = () => {
    const dispatch = useDispatch();

    const showResultGameModal = useSelector(state => state.gameContentModal.showResultGameModal);
    const finalScore = useSelector(state => state.gameContentModal.finalScore);

    useEffect(() => {
        const handleCloseModal = () => {
            dispatch(hideResultGame());
        };

        const handleRestartGame = () => {
            document.querySelector('.modal-result-game').style.display = 'none';
            dispatch(resetGame(true));
            dispatch(toggleGameContent(false));
            dispatch(toggleCountdownModal(true));
        };

        const handleCancelGame = () => {
            document.querySelector('.modal-result-game').style.display = 'none';

            // Disable all buttons except the ones with text "Play game"
            const buttons = document.querySelectorAll('button');
            buttons.forEach(button => {
                if (button.innerText !== 'Play game') {
                    button.disabled = true;
                }
            });
        };

        if (showResultGameModal) {
            document.querySelector('.modal-result-game').style.display = 'flex';
            document.querySelector('.final-score-text').innerText = finalScore;

            const restartButton = document.querySelector('.restart-game-text');
            const closeButton = document.querySelector('.result-game-button-close');
            const cancelButton = document.querySelector('.cancel-game-text');

            if (restartButton) {
                restartButton.addEventListener('click', handleRestartGame);
            }
            if (closeButton) {
                closeButton.addEventListener('click', handleCloseModal);
            }
            if (cancelButton) {
                cancelButton.addEventListener('click', handleCancelGame);
            }

            // Clean up event listeners on component unmount or update
            return () => {
                if (restartButton) {
                    restartButton.removeEventListener('click', handleRestartGame);
                }
                if (closeButton) {
                    closeButton.removeEventListener('click', handleCloseModal);
                }
                if (cancelButton) {
                    cancelButton.removeEventListener('click', handleCancelGame);
                }
            };
        }
    }, [showResultGameModal, finalScore, dispatch]);

    return (
        <div className="modal-result-game">
            <div className="result-game-wrapper">
                <div className="result-game-header">
                    <img src={bgStart} alt="Game result" />
                    <button className="result-game-button-close"></button>
                    <h2 id="your-own-score">Your Own Score</h2>
                </div>
                <h1 className="final-score-text">{finalScore}</h1>
                <div className="choices-group">
                    <button className="primary-button cancel-game-text">Cancel game</button>
                    <button className="primary-button restart-game-text">Restart game</button>
                </div>
                <div className="register-toggle-label">
                    <p>Register</p>
                </div>
            </div>
        </div>
    );
};

export default ResultGameModal;
