import React from 'react';
import { useSelector } from 'react-redux';
import '../Gamecontent/Gamecontent.css';
import coin from '../../assets/img/play-page/coin.png';
import { fetchData } from '../src/utils/api'; // Hàm tải dữ liệu
import { processData } from '../src/utils/dataUtils';

const Gamecontent = () => {
    const showGameContent = useSelector((state) => state.game.showGameContent);
    if (!showGameContent) return null;

    const dispatch = useDispatch();
    const { 
      listProducts, 
      listCharacters, 
      listProductNames, 
      levels, 
      currentScore, 
      currentLevel 
    } = useSelector((state) => state.game);

    // Load dữ liệu
    useEffect(() => {
        fetchData('src/assets/data.json')
            .then((data) => {
                processData(data, dispatch);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, [dispatch]);
  
    return (
        <div className="game-content"style={{ display: 'flex' }}>
            <div className="play-top-site row">
                <div className="play-top-site-left col-xl-6 col-lg-6 col-sm-6">
                    <div className="timer-site">
                        <div className="timer-range"></div>
                        <div className="level-site">
                            Level: <span id="level">1</span>
                        </div>
                    </div>
                </div>
                <div className="play-top-site-right col-xl-6 col-lg-6">
                    <div className="score-site">
                        <img className="crown-img" src={coin} alt="coin" />
                        <div className="current-score">0</div>
                    </div>
                </div>
            </div>

            <div className="row product-card-list" style={{ margin: '0' }}></div>
            <div className="row character-card-list" style={{ margin: '0' }}></div>
        </div>
    );
}
export default Gamecontent;
