import { configureStore, createSlice } from '@reduxjs/toolkit';

const gameSlice = createSlice({
  name: 'game',
  initialState: {
    listProducts: [],
    listCharacters: [],
    listProductNames: [],
    levels: [],
    timeStepPercentage: [],
    
    initTimeRemainder: 100,
    playTimes: 5,
    requiredNumberProducts: 0,
    maxRequiredProducts: 5,
    answerDone: false,
    timeStepDenominator: 10,

    // countdownTime: 3,
    // showCountdownModal: false,
    // intervalId: null,
    // showGameContent: false,

    gameTime: {
      timerInterval: null,
      timeRemainder: 100,
      timeStep: 10,
    },
    gameSound: {
      audioBackground: "assets/music/bg_sound.mp3",
      audioFail: "assets/music/wrong.mp3",
      maxVolume: 1,
      audioRatio: 1,
    },
    currentScore: 0,
    currentLevel: 1,
  },
  reducers: {
    updateProducts(state, action) {
      state.listProducts = action.payload;
    },
    updateCharacters(state, action) {
      state.listCharacters = action.payload;
    },
    updateProductNames(state, action) {
      state.listProductNames = action.payload;
    },
    updateLevels(state, action) {
      state.levels = action.payload;
    },
    updateTimeStepPercentage(state, action) {
      state.timeStepPercentage = action.payload;
    },

    setCountdownTime(state, action) {
        state.countdownTime = action.payload;
      },
      toggleCountdownModal(state, action) {
        state.showCountdownModal = action.payload;
      },
      setIntervalId(state, action) {
        state.intervalId = action.payload;
      },
      toggleGameContent(state, action) {
        state.showGameContent = action.payload;
      },

    resetGame(state) {
      // Đặt lại trạng thái game về mặc định
      state.listProducts = [];
      state.listCharacters = [];
      state.listProductNames = [];
      state.levels = [];
      state.timeStepPercentage = [];
      state.initTimeRemainder = 100;
      state.playTimes = 5;
      state.requiredNumberProducts = 0;
      state.maxRequiredProducts = 5;
      state.answerDone = false;
      state.timeStepDenominator = 10;
      state.gameTime = {
        timerInterval: null,
        timeRemainder: 100,
        timeStep: 10,
      };
      state.gameSound = {
        audioBackground: "/src/assets/music/bg_sound.mp3",
        audioFail: "src/assets/music/wrong.mp3",
        maxVolume: 1,
        audioRatio: 1,
      };
      state.currentScore = 0;
      state.currentLevel = 1;
    },
    incrementScore(state) {
      state.currentScore += 1;
    },
    incrementLevel(state) {
      state.currentLevel += 1;
    },
    setGameData(state, action) {
      state.listProducts = action.payload.products;
      state.listCharacters = action.payload.characters;
      state.levels = action.payload.levels;
    },
  },
});

export const {
  updateProducts,
  updateCharacters,
  updateProductNames,
  updateLevels,
  updateTimeStepPercentage,

  resetGame,
  incrementScore,
  incrementLevel,

  setCountdownTime,
  toggleCountdownModal,
  setIntervalId,
  toggleGameContent,
  setGameData,
} = gameSlice.actions;

export const store = configureStore({
    reducer: {
        game: gameSlice.reducer 
    }
});
