import { configureStore, createSlice } from '@reduxjs/toolkit';

const initialState = {
  listProducts: [],
  listCharacters: [],
  nameCorrectIndex: null,
  requiredNumberProducts: null,
  randomNameCorrect: '',

  audioBackground: new Audio("/music/bg_sound.mp3"),
  audioGameOver: new Audio("/music/wrong.mp3"),
  levels: [],
  timeStepPercentage: [],

  countdownTime: 3,
  showCountdownModal: false,
  intervalId: null,
  showGameContent: false,

  initTimeRemainder: 100,
  playTimes: 5,
  maxRequiredProducts: 5,
  answerDone: false,
  timeStepDenominator: 10,
  score: 0,

  gameTime: {
    timerInterval: null,
    timeRemainder: 100,
    timeStep: 10,
  },
  gameSound: {
    audioBackground: "/music/bg_sound.mp3",
    audioFail: "../../src/assets/music/wrong.mp3",
    maxVolume: 1,
    audioRatio: 1,
  },
  currentScore: 0,
  currentLevel: 1,
};

const gameSlice = createSlice({
  name: 'game',
  initialState,

  reducers: {
    updateProducts(state, action) {
      state.listProducts = action.payload;
    },
    updateCharacters(state, action) {
      state.listCharacters = action.payload;
    },
    updateLevels(state, action) {
      state.levels = action.payload;
    },
    updateTimeStepPercentage(state, action) {
      state.timeStepPercentage = action.payload;
    },
    setCountdownTime(state, action) {
      state.countdownTime = action.payload;
    },
    toggleCountdownModal(state, action) {
      state.showCountdownModal = action.payload;
    },
    setIntervalId(state, action) {
      state.intervalId = action.payload;
    },
    toggleGameContent(state, action) {
      state.showGameContent = action.payload;
    },
    resetGame(state) {
      return initialState;
    },
    incrementScore(state) {
      state.currentScore += 1;
    },
    incrementLevel(state) {
      state.currentLevel += 1;
    },
    setShowRandomProductName(state, action) {
      state.requiredProductName = action.payload;
    },
    setRandomProductNumber(state, action) {
      state.requiredProductNumber = action.payload;
    },
    setGameData(state, action) {
      state.listProducts = action.payload.products;
      state.listCharacters = action.payload.characters;
      state.levels = action.payload.levels;
    },

    handleCorrectAnswer: (state, action) => {
      const { productCard } = action.payload;
      const newValue = state.score + 1;
      state.score = newValue;
      // Cập nhật số lượng sản phẩm đúng đã chọn và hiển thị lên giao diện
      productCard.numberCorrect += 1;

      // Kiểm tra nếu người chơi chọn quá số lượng yêu cầu
      if (newValue > state.requiredNumberProducts) {
        state.showGameOver = true;
      } else if (newValue === state.requiredNumberProducts) {
        // Nếu đủ số lượng yêu cầu, reset và chuẩn bị cho câu hỏi tiếp theo
        state.resetAnswer();
        state.showRandomNameCorrect();
        state.showScoreCalculator();
        state.getAnswer();
        state.showCountdownTime();
      }
    },
    handleIncorrectAnswer: (state) => {
      console.log('You choosed incorrect!');
      state.showGameOver = true;
      // Cập nhật logic xử lý khi người chơi chọn sai
    },
    resetAnswer: (state) => {
      state.score = 0;
      // Đặt lại câu trả lời
      state.requiredNumberProducts = null;
      state.randomNameCorrect = '';
      state.gameTime.timeRemainder = state.gameTime.initTimeRemainder;
    },
    showRandomNameCorrect(state) {
      const randomIndex = Math.floor(Math.random() * state.listProducts.length);
      const maxRequiredProducts = 5;
      state.nameCorrectIndex = randomIndex;
      state.requiredNumberProducts = Math.floor(Math.random() * maxRequiredProducts) + 1;
      state.randomNameCorrect = state.listProducts[randomIndex]?.title || '';
    },
    setAudioBackground: (state, action) => {
      state.audioBackground = new Audio(action.payload);
    },
    setAudioGameOver: (state, action) =>{
      state.audioGameOver = new Audio(action.payload);
    },
    playBackgroundSound: (state) => {
      if (state.audioBackground) {
        state.audioBackground.play().catch(error => {
          console.error('Audio play failed:', error);
        });
      }
    },
    
  },
});

export const {
  updateProducts,
  updateCharacters,
  updateLevels,
  updateTimeStepPercentage,
  resetGame,
  incrementScore,
  incrementLevel,
  setCountdownTime,
  toggleCountdownModal,
  setIntervalId,
  toggleGameContent,
  setShowRandomProductName,
  setRandomProductNumber,
  setGameData,
  showRandomNameCorrect,
  handleCorrectAnswer,
  handleIncorrectAnswer,
  resetAnswer,

  setAudioBackground, 
  playBackgroundSound
} = gameSlice.actions;


export const store = configureStore({
  reducer: {
    game: gameSlice.reducer,
  },
});
