import { configureStore, createSlice } from '@reduxjs/toolkit';

const initialState = {
  // Load data
  listProducts: [],
  listCharacters: [],
  // Random
  nameCorrectIndex: null,
  requiredNumberProducts: null,
  randomNameCorrect: '',
  // Audio
  audioBackground: new Audio("/music/bg_sound.mp3"),
  audioFail: new Audio("/music/wrong.mp3"),
  // Countdown Time
  levels: [],
  timeStepPercentage: [],
  // Countdown
  countdownTime: 3,
  showCountdownModal: false,
  intervalId: null,
  showGameContent: false,
  // Error
  audioBackgroundPaused: false,
  showErrorModal: false,
  // Result
  showResultGameModal: false,
  finalScore: 0,
  // Reset Answer
  numberCorrect: 0,
  nameCorrectDesc: '',
  numberCorrectDisplay: 'none',

  initTimeRemainder: 100,
  // playTimes: 5,
  // maxRequiredProducts: 5,
  // answerDone: false,

  score: 0,

  gameTime: {
    timerInterval: null,
    timeRemainder: 100,
    timerRangeWidth: '100%',
    timeStep:  10,
  },
  gameSound: {
    audioBackground: "/music/bg_sound.mp3",
    audioFail: "music/wrong.mp3",
    maxVolume: 1,
    audioRatio: 1,
  },
  // Update Level
  currentScore: 0,
  currentLevel: 1,

};

const gameSlice = createSlice({
  name: 'game',
  initialState,

  reducers: {
    // Load data
    updateProducts(state, action) {
      state.listProducts = action.payload;
    },
    updateCharacters(state, action) {
      state.listCharacters = action.payload;
    },
    updateLevels(state, action) {
      state.levels = action.payload;
    },
    updateTimeStepPercentage(state, action) {
      state.timeStepPercentage = action.payload;
    },
    setTimerInterval(state, action) {
      state.gameTime.timerInterval = action.payload;
    },
    // Countdown Time
    showCountdownTime(state) {
      state.gameTime.timeRemainder -= state.gameTime.timeStep;
      state.gameTime.timerRangeWidth = Math.max(state.gameTime.timeRemainder / state.gameTime.timeStep * 10, 0) + '%';
      if (state.gameTime.timeRemainder <= 0) {
        clearInterval(state.gameTime.timerInterval);
        state.showErrorModal = true;
      }
    },
    setCountdownTime(state, action) {
      state.countdownTime = action.payload;
    },
    resetCountdownTime(state) {
      state.gameTime.timeRemainder = state.initTimeRemainder;
      state.timerRangeWidth = '100%';
      clearInterval(state.gameTime.timerInterval);
    },
    toggleCountdownModal(state, action) {
      state.showCountdownModal = action.payload;
    },
    // Error
    showGameOver(state) {
      clearInterval(state.gameTime.timerInterval);
      state.showErrorModal = true;
    },
    hideErrorModal(state) {
      state.showErrorModal = false;
    },
    // Result
    showResultGame(state) {
      state.showResultGameModal = true;
      state.finalScore = state.currentScore;
    },
    hideResultModal(state) {
      state.showResultGameModal = false;
    },
    incrementScore(state) {
      state.currentScore += 1;
    },
    updateGameLevel(state) {
      state.currentLevel = Math.floor(state.currentScore / 3) + 1;
      const levelData = state.levels.find(level => level.level === state.currentLevel);
      if (levelData) {
        state.timeStepDenominator = levelData.timeStepPercentage;
        state.gameTime.timeStep = state.initTimeRemainder / (100 / state.timeStepDenominator);
      }
    },
    //
    setIntervalId(state, action) {
      state.intervalId = action.payload;
    },
    clearGameInterval(state) {
      if (state.intervalId) {
        clearInterval(state.intervalId);
        state.intervalId = null;
      }
    },
    //
    toggleGameContent(state, action) {
      state.showGameContent = action.payload;
    },
    resetGame(state) {
      return initialState;
    },
    setShowRandomProductName(state, action) {
      state.requiredProductName = action.payload;
    },
    setRandomProductNumber(state, action) {
      state.requiredProductNumber = action.payload;
    },
    setGameData(state, action) {
      state.listProducts = action.payload.products;
      state.listCharacters = action.payload.characters;
      state.levels = action.payload.levels;
    },







    getAnswer(state, action) {
      const { productCard } = action.payload;
      const requireProduct = state.randomNameCorrect.toLowerCase();
      const productTitle = productCard.querySelector('.product-card-name').innerText.toLowerCase();
    
      if (productTitle === requireProduct) {
        state.numberCorrect += 1;
        state.numberCorrectDisplay = 'flex';
    
        if (state.numberCorrect === state.requiredNumberProducts) {
          setTimeout(() => {
            state.numberCorrect = 0;
            state.randomNameCorrect = '';
            state.numberCorrectDisplay = 'none';
            state.currentScore += 1;
            state.currentLevel = Math.floor(state.currentScore / 3) + 1;
            state.showRandomNameCorrect();
            state.resetCountdownTime();
          }, 300);
        }
      } else {
        state.audioBackgroundPaused = true;
        state.showErrorModal = true;
        state.playSoundGameOver = true;
      }
    },









    //
    // handleCorrectAnswer(state, action) {
    //   const { productCard } = action.payload;
    //   const newValue = state.score + 1;
    //   state.score = newValue;
    //   productCard.numberCorrect += 1;
    //   if (newValue > state.requiredNumberProducts) {
    //     state.showGameOver = true;
    //   } else if (newValue === state.requiredNumberProducts) {
    //     state.resetAnswer();
    //     state.randomNameCorrect();
    //     state.updateGameLevel();
    //     state.getAnswer();
    //     state.countdownTime();
    //   }
    // },
    // handleIncorrectAnswer(state) {
    //   state.showGameOver = false;
     
    //   state.audioBackground.pause();
    //   state.audioFail.play();
    // },
    resetAnswer(state) {
      state.numberCorrect = 0;
      state.nameCorrectDesc = '';
      state.numberCorrectDisplay = 'none';
      state.gameTime.timeRemainder = state.initTimeRemainder;
      state.timerRangeWidth = '100%';
      clearInterval(state.gameTime.timerInterval);
    },
    showRandomNameCorrect(state) {
      const randomIndex = Math.floor(Math.random() * state.listProducts.length);
      const maxRequiredProducts = 5;
      state.nameCorrectIndex = randomIndex;
      state.requiredNumberProducts = Math.floor(Math.random() * maxRequiredProducts) + 1;
      state.randomNameCorrect = state.listProducts[randomIndex]?.title || '';
    },
    setAudioBackground: (state, action) => {
      state.audioBackground = new Audio(action.payload);
    },
    setAudioFail: (state, action) => {
      state.audioFail = new Audio(action.payload);
    },
    playBackgroundSound: (state) => {
      if (state.audioBackground) {
        state.audioBackground.play().catch(error => {
          console.error('Audio play failed:', error);
        });
      }
    },
    playSoundGameOver: (state) => {
      if (state.audioFail) {
        state.audioFail.play().catch(error => {
          console.error('Audio play failed:', error);
        });
      }
    },
    playbackRateAudioBackground: (state, action) => {
      if (state.audioBackground) {
        state.audioBackground.playbackRate = action.payload;
      }
    },

  },
});

export const {
  updateProducts,
  updateCharacters,
  updateLevels,
  updateTimeStepPercentage,
  resetGame,

  setCountdownTime,
  toggleCountdownModal,

  setIntervalId,
  clearGameInterval,

  setTimerInterval,
  showCountdownTime,
  resetCountdownTime,

  showGameOver,
  hideErrorModal,

  showResultGame,
  hideResultModal,

  toggleGameContent,
  setShowRandomProductName,
  setRandomProductNumber,
  setGameData,
  showRandomNameCorrect,

  handleCorrectAnswer,
  handleIncorrectAnswer,
  resetAnswer,

  setAudioFail,
  playBackgroundSound,
  playSoundGameOver,
  playbackRateAudioBackground,

  incrementScore,
  updateGameLevel,

  getAnswer
} = gameSlice.actions;


export const store = configureStore({
  reducer: {
    game: gameSlice.reducer,
  },
});
