import React, { useEffect } from 'react';
import '../ErrorModal/ErrorModal.css'
import bgError from '../../assets/img/general/bg-error.png';
import {playSoundGameOver, showGameOver, hideErrorModal, } from '../../store/gameSlice';
import {useSelector, useDispatch } from 'react-redux';

function ErrorModal() {
    const dispatch = useDispatch();
    const showErrorModal = useSelector((state) => state.game.showErrorModal);
    const handleGameOver = () => {
        dispatch(playSoundGameOver());
      };
      

      useEffect(() => {
        // Kiểm tra nếu showErrorModal là true
        if (showErrorModal) {
          // Ẩn modal thông báo lỗi sau 3 giây và hiển thị kết quả game
          setTimeout(() => {
            // Gửi action để ẩn modal thông báo lỗi
            dispatch(hideErrorModal());
            // Gửi action để hiển thị kết quả game
            dispatch(showResultGame(true));
          }, 3000);
        }
      }, [showErrorModal, dispatch]);
      
      // Nếu showErrorModal là false, trả về null để không render component
      if (!showErrorModal) return null;
    return(
        <div className="false-clicked-modal" style={{ display: showErrorModal ? 'flex' : 'none' }}>
            <img src={bgError} alt="false-clicked" 
            className="false-clicked-ing"/>
        </div>
    );
}

export default ErrorModal;
