import React from 'react';
import { useDispatch } from 'react-redux';
import { toggleCountdownModal } from '../store/gameSlice';
import CountdownModal from './CountdownModal';
import Gamecontent from '../Gamecontent/Gamecontent';
import '../Header/Header.css';
import takashimayaLogo from '../../assets/img/general/logo.png';

const Header = () => {
  const dispatch = useDispatch();
  const startGame = () => {
    dispatch(toggleCountdownModal(true));
  };
  return (
    <header className="header-site">
      <nav class="navbar">
        <div class="container header-item-list">
          <a href="#home-page" class="takashimaya-header-logo">
            <img src={takashimayaLogo} alt="Takashimaya-logo" />
          </a>
          <ul class="nav" id="header-navbar">
            <li class="nav-item">
              <a class="nav-link" onClick={startGame} href="#play-page">Play game</a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#rules-page">Rules</a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#prize-page">Prize</a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#rank-page">Rank</a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#setting-page">Setting</a>
            </li>
          </ul>
        </div>
      </nav>
    </header>
  );
}
export default Header;
