import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
    setCountdownTime,
    toggleCountdownModal,
    setIntervalId,
    toggleGameContent,
} from '../../store/gameSlice';
import '../CountdownModal/CountdownModal.css'

const CountdownModal = ({ onCountdownComplete }) => {
    const [countdownTime, setCountdownTime] = useState(3);
    const [showCountdownModal, setShowCountdownModal] = useState(true);
useEffect(() => {
    if (showCountdownModal) {
        const initCountDownTime = 3;
        setCountdownTime(initCountDownTime);

        const id = setInterval(() => {
            setCountdownTime((prevTime) => {
                const newTime = prevTime - 1;

                if (newTime <= 0) {
                    clearInterval(id);
                    setShowCountdownModal(false);
                    onCountdownComplete();
                }
                return newTime;
            });
        }, 1000);

        return () => clearInterval(id);
    }
}, [showCountdownModal, onCountdownComplete]);

if (!showCountdownModal) return null;
    return (
        <div className="modal-count-down">
            <span>Count Down Game</span>
            <h1 className="count-down-text">{countdownTime}</h1>
        </div>

    );
};
export default CountdownModal;
