import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setAudioBackground, playBackgroundSound } from '../../store/gameSlice'; // Đảm bảo đường dẫn đúng đến gameSlice
import Toolbar from '../Toolbar/Toolbar';
import GamecontentModal from '../GameContentModal/GameContentModal';
import ResultGameModal from '../ResultGameModal/ResultGameModal';
import CountdownModal from '../CountdownModal/CountdownModal';

function PlayPage() {
  const dispatch = useDispatch();

  useEffect(() => {
    // Đặt đường dẫn âm thanh nền khi component được mount
    dispatch(setAudioBackground("../../../public/music/bg_sound.["));

    // Hàm tự động chạy action sau 3 giây
    const timer = setTimeout(() => {
      dispatch(playBackgroundSound());
    }, 3000);

    return () => clearTimeout(timer); // Xóa timer khi component unmount
  }, [dispatch]);

  return (
    <div className="main-bottom-site">
      <div className="play-page-main-bottom-site">
        <div className="container main-bottom-content-row">
          <div className="play-page" id="play-page">
            <audio className="audio-background"></audio>
            <audio className="audio-fail"></audio>

            <ResultGameModal />
            <CountdownModal />
            <GamecontentModal />
            <Toolbar />
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayPage;
