import '../Main/Main.css'
import PlayPage from '../PlayPage/PlayPage';
import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setAudioBackground } from '../../store/gameSlice';

const Main = () => {
    const dispatch = useDispatch();

    useEffect(() => {
      // Đặt đường dẫn âm thanh nền khi component được mount
      dispatch(setAudioBackground("../../src/assets/music/bg_sound.mp3"));
  
      // Hàm tự động chạy action sau 3 giây
      const timer = setTimeout(() => {
        dispatch(playBackgroundSound());
      }, 3000);
  
      return () => clearTimeout(timer); // Xóa timer khi component unmount
    }, [dispatch]);
    return (
        <main className="main-site">
            <PlayPage/>
        </main>
    );
}

export default Main;
