import { createSlice } from '@reduxjs/toolkit';
import audioManager from '../../utils/audioManager';

const countdownModalInitialState = {
  countdownTime: 3,
  showCountdownModal: false,
  intervalId: null,
};

const countdownModalSlice = createSlice({
  name: 'countdownModal',
  initialState: countdownModalInitialState,
  reducers: {
    setCountdownTimeModal(state, action) {
      state.countdownTime = action.payload;
      audioManager.playBackgroundSound();
    },
    toggleCountdownModal(state, action) {
      state.showCountdownModal = action.payload;
      
    },
    setIntervalId(state, action) {
      state.intervalId = action.payload;
    },
    clearGameInterval(state) {
      if (state.intervalId) {
        clearInterval(state.intervalId);
        state.intervalId = null;
      }
    },
  },
});

export const {
  setCountdownTimeModal,
  toggleCountdownModal,
  setIntervalId,
  clearGameInterval,

} = countdownModalSlice.actions;

export default countdownModalSlice.reducer;
