import { configureStore } from '@reduxjs/toolkit';
import countdownModalReducer from '../components/CountdownModal/CountdownModalSlice';
import errorModalReducer from '../components/ErrorModal/ErrorModalSlice';
import gameContentModalReducer from '../components/GameContentModal/GameContentModalSlice';
import resultGameModalReducer from '../components/ResultGameModal/ResultGameModalSlice';

export const store = configureStore({
  reducer: {
    countdownModal: countdownModalReducer,
    errorModal: errorModalReducer,
    gameContentModal: gameContentModalReducer,
   
    resultGameModal: resultGameModalReducer,
    toolbar: toolbarReducer,
  },
});
